/*
	Class level decorator that marks a Property subclass as needing to automount when
	the parent is shadowed. This is useful for implementing mixin properties that provide
	behavior for the parent property. Using automount will cause the child property to
	be immediately shadowed and not wait for the app to access it.

	Example:
		@automount
		class PostopSubscriptionMixinProperty extends ObjectProperty {
			...
		}
*/
export default function automount(target) {
	if (!target.prototype) {
		throw new SyntaxError("@automount() must be applied to a class.");
	}

	target.shouldAutomount = function() { return true }
}

import Property from "../Property";

/*
	Adds a mixin to a Property subclass. Mixins are a handy way to share life-cycle and shadow functionality
	across properties without using inheritance (which is fine too). A factory function is passed as the
	single parameter and returns a plain javascript object (https://lodash.com/docs#isPlainObject). The
	mixin object can have:
		* property lifecycle methods - these methods will be invoked before the property's implementation.
		* @shadow marked methods - these methods will be mapped onto the shadow after the actual property's
			shadow methods have been defined.
		* other methods - these will not be mapped onto the property or shadow so there is no chance of
			conflict with the property, shadow, or other mixins.

	A minimal mixin example:

		function mixinFactory(property) {
			// perform sub-property setup on the property (this example assumes ObjectProperty subclass)
			property.addPropertyClass(_connected, PrimitiveProperty, false, true);

			return {
				// lifecycle method
				propertyDidShadow() {
					console.log("Mixin propertyDidShadow() called");
				}

				// shadow method
				@shadow
				echo(msg) {
					console.log("Mixin echos: " + msg);
				}
			}
		}

	Use the mixin using the @mixin() decorator:

		@mixin(mixinFactory)
		class UsersCollection extends CollectionProperty { ... }

	Access the mixin shadow methods as normal:

		users.echo("Hello mixin");


	Parameter:
		mixinFactory - a function of the form: factory(property) : POJO
*/
export default function mixin(mixinFactory) {
	return function registerMixin(target, name, descriptor) {
		const p = Property;

		if (!Property.isPrototypeOf(target)) {
			throw new SyntaxError("@mixin() must be applied to a Property subclass.");
		}

		if (!target.mixins) {
			target.mixins = [];
		}

		target.mixins.push(mixinFactory);
	}
}

import shadowPropertyHelper from "./shadowPropertyHelper";


/*
	A Property decorator for marking methods and properties (get/set) as being part of the shadow. 

	Example:

		@shadow
		get user() { 
			return { name: "Andy" } 
		}

		@shadow
		isAdmin() {
			return true;
		}

*/
export default function shadow(target, name, descriptor) {
	shadowPropertyHelper(target, name, descriptor);
}

import shadowPropertyHelper from "./shadowPropertyHelper";


/*
	A Property decorator for marking methods and properties (get/set) as being part of the shadow with
	the methods *bound* to the property. Use this decorator version when you Property method is not
	dependent on using the state at the time the shadow reference may have been saved to a veriable
	or you method is requires a majority of accesses to the Property subclass instance variables 
	instead of the shadow state.

	Example:

		@shadowBound
		logout() {
			// delete is a method in the Property subclass and not dependent on the current state
			this.delete(_user);
		}

*/
export default function shadowBound(target, name, descriptor) {
	shadowPropertyHelper(target, name, descriptor, true);
}

import isPlainObject from "lodash.isplainobject";


export default function shadowPropertyHelper(target, name, descriptor, bindProperty=false) {
	if (isPlainObject(target)) {
		// mixin method
		if (!target.__shadowDescriptors__) {
			target.__shadowDescriptors__ = {};
		}

		addDescriptor(target.__shadowDescriptors__, name, descriptor, bindProperty);
	} else {
		// Property subclass method
		const ctor = target.constructor;

		if (!ctor.shadowDescriptors) {
			ctor.shadowDescriptors = {};
		}

		addDescriptor(ctor.shadowDescriptors, name, descriptor, bindProperty);
	}
}


function addDescriptor(shadowDescriptors, name, descriptor, bindProperty) {
	shadowDescriptors[name] = { descriptor, bindProperty };
}

export { default as automount } from "./decorators/automount";
export { default as mixin } from "./decorators/mixin";
export { default as shadow } from "./decorators/shadow";
export { default as shadowBound } from "./decorators/shadowBound";

import has from "lodash.has";
import isFunction from "lodash.isfunction";

/**
	Extends a shadow property with the definition returned by the assoicated Property methods
	marked using the @shadow and @shadowBound decorators.

	NOTE: function must be called after subclasses are created.

	@ignore
*/
export default function extendProperty(property, impl, shadow) {
	const proto = Object.getPrototypeOf(property);
	const shadowDescriptors = proto.constructor.shadowDescriptors;

	if (shadowDescriptors) {
		defineProperties(shadowDescriptors, shadow, property, impl);
	}

	// define mixin shadow properties
	var mixins, mixinDescriptors, mixinDesc;

	if (property.__hasMixins()) {
		let mixins = property.__mixins();

		for (let i=0, len=mixins.length; i<len; i++) {
			mixinDescriptors = mixins[i].__shadowDescriptors__;

			if (mixinDescriptors) {
				defineProperties(mixinDescriptors, shadow, property, impl, true);
			}
		}
	}
}

function defineProperties(shadowDescriptors, shadow, property, impl, mixin) {
	for (let name in shadowDescriptors) {
		if (has(shadow, name) || shadow[name]) {
			console.warn(`Property name conflict: name=${name}, path=${impl.slashPath()}, mixin-source=${!!mixin}`);

			continue;
		}

		const { descriptor, bindProperty } = shadowDescriptors[name];

		if (isFunction(descriptor.value)) {
			const value = bindProperty ?descriptor.value.bind(property) :descriptor.value;

			// Functions are not enumerable in javascript classes so adher to convention
			Object.defineProperty(shadow, name, {
					enumerable: false,
					value: value
				});
		} else {
			const enumerable = descriptor.enumerable && !name.startsWith('_');
			const get = bindProperty && descriptor.get ?descriptor.get.bind(property) :descriptor.get
			const set = bindProperty && descriptor.set ?descriptor.set.bind(property) :descriptor.set

			if (descriptor.get) {
				Object.defineProperty(shadow, name, {
						enumerable: enumerable,
						get: get, // && descriptor.get.bind(property),
						set: set, // && descriptor.set.bind(property),
					});
			} else {
				Object.defineProperty(shadow, name, {
						enumerable: enumerable,
						value: descriptor.value,
						writable: false,
					});
			}
		}
	}
}

